function setThemeColor(){let e=huePicker.value,t=document.querySelector(":where(html)"),r=document.getElementById("hue-value");t.style.setProperty("--brand-hue",e),r.textContent=e}function initializeGrid(){setGridSize(currentGridSize),createGrid()}function setGridSize(e){grid.setAttribute("style",`width: calc(${e} * var(--size-base) )`)}function createGrid(){const e=currentGridSize*currentGridSize;for(var t=0;t<e;t+=1){const e=document.createElement("div");e.addEventListener("mouseover",changeColor),e.className="pixel",grid.appendChild(e)}}function changeColor(e){e.target.style.backgroundColor=currentColor}function resetColor(){let e=document.querySelectorAll(".pixel");e.forEach(e=>e.removeAttribute("style"))}const DEFAULT_GRID_SIZE=32,DEFAULT_COLOR="var(--brand)";let currentColor=DEFAULT_COLOR,currentGridSize=DEFAULT_GRID_SIZE,grid=document.getElementById("grid"),btnReset=document.getElementById("reset-button"),huePicker=document.getElementById("hue-picker");huePicker.addEventListener("input",setThemeColor),btnReset.addEventListener("click",resetColor),initializeGrid();