function setThemeColor(){let e=document.querySelector(":where(html)"),t=getById("hue-value"),r=huePicker.value;e.style.setProperty("--brand-hue",r),t.textContent=r}function setGridSize(e){grid.style.cssText=`\n  grid-template-rows: repeat(${e}, 1fr);\n  grid-template-columns: repeat(${e}, 1fr);\n  width: calc(var(--size-base) * ${e})\n  `}function createGrid(){const e=chosenGridSize*chosenGridSize;for(var t=0;t<e;t+=1){const e=document.createElement("div");e.addEventListener("mouseover",changeColor),e.className="pixel",grid.appendChild(e)}}function changeColor(e){e.target.style.backgroundColor=chosenColor}function resetColor(){let e=document.querySelectorAll(".pixel");e.forEach(e=>e.removeAttribute("style"))}function initializeGrid(){setGridSize(chosenGridSize),createGrid()}const getById=e=>document.getElementById(e),DEFAULT_GRID_SIZE=21,DEFAULT_COLOR="var(--brand)";let chosenGridSize=DEFAULT_GRID_SIZE,chosenColor=DEFAULT_COLOR,grid=getById("grid"),btnReset=getById("reset-button"),huePicker=getById("hue-picker");huePicker.addEventListener("input",setThemeColor),btnReset.addEventListener("click",resetColor),initializeGrid();